        [Test]
        public void Part1_Examples()
        {
            Assert.AreEqual(sut.follow("(())"), 0);
            Assert.AreEqual(sut.follow("()()"), 0);

            Assert.AreEqual(sut.follow("((("), 3);
            Assert.AreEqual(sut.follow("(()(()("), 3);
            Assert.AreEqual(sut.follow("))((((("), 3);


            Assert.AreEqual(sut.follow("())"), -1);
            Assert.AreEqual(sut.follow("))("), -1);

            Assert.AreEqual(sut.follow(")))"), -3);
            Assert.AreEqual(sut.follow(")())())"), -3);
        }

