        /// <summary>
        /// </summary>
        /// <param name="instructions">
        /// </param>
        /// <returns>
        /// </returns>
        private int follow(string instructions)
        {
            var steps = instructions.ToCharArray();
            return steps.Count(s => s.Equals('(')) - steps.Count(s => s.Equals(')'));
        }

        /// <summary>
        /// </summary>
        /// <param name="instructions">
        /// </param>
        /// <param name="pointer">
        /// </param>
        /// <param name="level">
        /// </param>
        /// <param name="required">
        /// </param>
        /// <returns>
        /// </returns>
        private int followTo(string instructions, int pointer, int level, int required)
        {
            if (level == required)
            {
                return pointer;
            }

            if (instructions[pointer] == '(')
            {
                level++;
            }
            else if (instructions[pointer] == ')')
            {
                level--;
            }

            pointer++;

            return this.followTo(instructions, pointer, level, required);
        }

